import React, { useState, useCallback, useRef } from "react";
import  {ReactFlow,
  ReactFlowProvider,
  Controls,
  MiniMap,
  Background,
  addEdge,
} from "@xyflow/react";

export const Editor = () => {
    const initialNodes = [
        { id: "1", type: "input", position: { x: 250, y: 100 }, data: { label: "Start Node" } , style:{border:'2px solid #0041d0',borderRadius:'50%',padding:10}}
      ];
      
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState([]);
  const reactFlowWrapper = useRef(null);

  const onNodesChange = useCallback(
    (changes) => setNodes((nds) => nds.map((node) => ({ ...node, ...changes }))),
    []
  );

  const onEdgesChange = useCallback((changes) => setEdges((eds) => addEdge(changes, eds)), []);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    []
  );

  const addRectangle = () => {
    setNodes((nds) => [
      ...nds,
      { id: `${nds.length + 1}`, position: { x: 100, y: 100 }, data: { label: "Rectangle" } }
    ]);
  };

  const addCircle = () => {
    setNodes((nds) => [
      ...nds,
      { id: `${nds.length + 1}`, position: { x: 200, y: 200 }, data: { label: "Circle" } }
    ]);
  };

  const downloadImage = () => {
    const canvas = document.querySelector("canvas");
    const dataUrl = canvas.toDataURL("image/png");

    const link = document.createElement("a");
    link.href = dataUrl;
    link.download = "flowchart.png";
    link.click();
  };

  return (
    <ReactFlowProvider>
      <div style={{ height: "100vh", width: "100%" }} ref={reactFlowWrapper}>
        <div style={{ position: "absolute", zIndex: 10, top: 10, left: 10 }}>
          <button onClick={addRectangle} style={{ marginRight: 5 }}>Rectangle</button>
          <button onClick={addCircle} style={{ marginRight: 5 }}>Circle</button>
          <button onClick={downloadImage} style={{ marginRight: 5 }}>Download</button>
        </div>

        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          fitView
        >
          <MiniMap />
          <Controls />
          <Background color="#aaa" gap={16} />
        </ReactFlow>
      </div>
    </ReactFlowProvider>
  );
};
