import React, { useState, useCallback } from "react";
import ReactFlow, {
  ReactFlowProvider,
  Controls,
  MiniMap,
  Background,
  addEdge,
  useNodesState,
  useEdgesState,
} from "@xyflow/react";
import "@xyflow/react/dist/style.css";
import { v4 as uuidv4 } from "uuid";

const initialNodes = [];
const initialEdges = [];

export const Editor = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNode, setSelectedNode] = useState(null);

  const addShape = (shape) => {
    const newNode = {
      id: uuidv4(),
      type: "default",
      data: { label: "Edit Text" },
      position: { x: Math.random() * 400, y: Math.random() * 400 },
      style: shapeStyles[shape],
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), [setEdges]);

  const handleTextChange = (event) => {
    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNode
          ? { ...node, data: { ...node.data, label: event.target.value } }
          : node
      )
    );
  };

  return (
    <div style={{ height: "100vh", width: "100%" }}>
      <div style={{ position: "absolute", zIndex: 10, top: 10, left: 10 }}>
        {["Rectangle", "Circle", "Triangle", "Diamond", "Hexagon", "Star", "Pentagon", "Parallelogram", "Trapezoid", "Ellipse"].map((shape) => (
          <button key={shape} style={{ marginRight: 5 }} onClick={() => addShape(shape)}>
            {shape}
          </button>
        ))}
        <button style={{ marginRight: 5 }} onClick={() => console.log("Download Logic")}>Download</button>
      </div>
      {selectedNode && (
        <input
          style={{ position: "absolute", top: 50, left: 10, zIndex: 10 }}
          value={nodes.find((node) => node.id === selectedNode)?.data?.label || ""}
          onChange={handleTextChange}
        />
      )}
      <ReactFlowProvider>
        <div style={{ height: "100%", width: "100%" }}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={(_, node) => setSelectedNode(node.id)}
            fitView
          >
            <MiniMap />
            <Controls />
            <Background color="#aaa" gap={16} />
          </ReactFlow>
        </div>
      </ReactFlowProvider>
    </div>
  );
};

const shapeStyles = {
  Rectangle: { width: 100, height: 60, background: "#F4A261" },
  Circle: { width: 60, height: 60, borderRadius: "50%", background: "#2A9D8F" },
  Triangle: { width: 0, height: 0, borderLeft: "40px solid transparent", borderRight: "40px solid transparent", borderBottom: "70px solid #E76F51" },
  Diamond: { width: 70, height: 70, transform: "rotate(45deg)", background: "#264653" },
  Hexagon: { width: 80, height: 46, background: "#E9C46A", clipPath: "polygon(25% 0%, 75% 0%, 100% 50%, 75% 100%, 25% 100%, 0% 50%)" },
  Star: { width: 0, height: 0, borderLeft: "40px solid transparent", borderRight: "40px solid transparent", borderBottom: "70px solid #8A2BE2" },
  Pentagon: { width: 70, height: 70, background: "#D7263D", clipPath: "polygon(50% 0%, 100% 38%, 82% 100%, 18% 100%, 0% 38%)" },
  Parallelogram: { width: 100, height: 60, background: "#4CAF50", transform: "skew(20deg)" },
  Trapezoid: { width: 100, height: 60, background: "#FF9800", clipPath: "polygon(25% 0%, 75% 0%, 100% 100%, 0% 100%)" },
  Ellipse: { width: 100, height: 60, background: "#2196F3", borderRadius: "50%" },
};
